import React, { useState } from 'react'
import Connexion from './Connexion';
import Inscription from './Inscription';
import '../../styles/pages/_profil.scss'

const Log = ( props ) => {
    const [signUpModal, setSignUpModal] = useState(props.connexion);
    const [signInModal, setSignInModal] = useState(props.inscription);
    const [signInDefault, setSignInDefault] = useState(props.inscription);

    const handleModals = (e) => {
        if (e.target.id === "register") {
          setSignInModal(false);
          setSignUpModal(true);
        } else if (e.target.id === "login") {
          setSignUpModal(false);
          setSignInModal(true);
        }
      };
    
    return (
        <div className="connection-form">
            <div className="form-container">
                <div className="info-form-container">
                    <ul>
                        <li>Bienvenue !</li>
                        <li>
                            <div className='description-type-connection'>
                                <p>Vous ne disposez pas</p> 
                                <p>de compte</p>
                            </div>
                        </li>
                        <li
                            onClick={handleModals}
                            id="register"
                            className={signUpModal ? "active-btn button-form" : "button-form"}
                        >
                            S'inscrire
                        </li>
                        <li>
                            <div className='description-type-connection'>
                                <p>Vous possédez déjà</p>
                                <p>un compte</p>       
                            </div>     
                        </li>
                        <li
                            onClick={handleModals}
                            id="login"
                            className={signInModal ? "active-btn button-form" : "button-form"}
                        >
                            Se connecter
                        </li>
                    </ul>
                </div>
                {(signUpModal && <Inscription />) || (signupdefault===true && <Inscription />)}
                {signInModal && <Connexion />}
            </div>
        </div>
    );
};

export default Log;
